fix-group.fix:
CONF_FILE=/etc/crypto-policies/back-ends/gnutls.config correct_value='+VERS-ALL:-VERS-DTLS0.9:-VERS-SSL3.0:-VERS-TLS1.0:-VERS-TLS1.1:-VERS-DTLS1.0'
grep -q ${correct_value} ${CONF_FILE}
if [[ $? -ne 0 ]]; then # We need to get the existing value, using PCRE to maintain same regex existing_value=$(grep -Po '(\+VERS-ALL(?::-VERS-[A-Z]+\d\.\d)+)' ${CONF_FILE})
if [[ ! -z ${existing_value} ]]; then # replace existing_value with correct_value sed -i "s/${existing_value}/${correct_value}/g" ${CONF_FILE} else # ***NOTE*** # # This probably means this file is not here or it's been modified # unintentionally. # ********** # # echo correct_value to end echo ${correct_value} >> ${CONF_FILE} fi fi

# Remediation is applicable only in certain platforms if [ ! -f /.dockerenv ] && [ ! -f /run/.containerenv ]; then
function perform_remediation {
mount_point_match_regexp="$(printf "[[:space:]]%s[[:space:]]" "/boot")"
grep "$mount_point_match_regexp" -q /etc/fstab \ || { echo "The mount point '/boot' is not even in /etc/fstab, so we can't set up mount options" >&2; echo "Not remediating, because there is no record of /boot in /etc/fstab" >&2; return 1; }
mount_point_match_regexp="$(printf "[[:space:]]%s[[:space:]]" /boot)"
# If the mount point is not in /etc/fstab, get previous mount options from /etc/mtab if [ "$(grep -c "$mount_point_match_regexp" /etc/fstab)" -eq 0 ]; then # runtime opts without some automatic kernel/userspace-added defaults previous_mount_opts=$(grep "$mount_point_match_regexp" /etc/mtab | head -1 | awk '{print $4}' \ | sed -E "s/(rw|defaults|seclabel|noauto)(,|$)//g;s/,$//") [ "$previous_mount_opts" ] && previous_mount_opts+="," echo " /boot defaults,${previous_mount_opts}noauto 0 0" >> /etc/fstab # If the mount_opt option is not already in the mount point's /etc/fstab entry, add it elif [ "$(grep "$mount_point_match_regexp" /etc/fstab | grep -c "noauto")" -eq 0 ]; then previous_mount_opts=$(grep "$mount_point_match_regexp" /etc/fstab | awk '{print $4}') sed -i "s|\(${mount_point_match_regexp}.*${previous_mount_opts}\)|\1,noauto|" /etc/fstab fi
if mkdir -p "/boot"; then if mountpoint -q "/boot"; then mount -o remount --target "/boot" else mount --target "/boot" fi fi }
perform_remediation
else >&2 echo 'Remediation is not applicable, nothing was done' fi

