)]*#ldap_id_use_start_tls = true#" $SSSD_CONF elif grep -qs $DOMAIN_REGEX $SSSD_CONF; then sed -i "/$DOMAIN_REGEX/a ldap_id_use_start_tls = true" $SSSD_CONF else if test -f "$SSSD_CONF"; then echo -e "[domain/default]
